import React from 'react';
import {useForm} from "react-hook-form";

const AddTest = () => {
 const {register, handleSubmit} = useForm()

    const addQuset = (data) => {

     const answers = Object.keys(data).filter( it => it.startsWith("answer")).map(it => data[it])

     const question = {
         question: data.question,
         points: data.points,
         answers
     }
    }

    return (
        <div className="container d-flex align-items-center justify-content-center">

            <form onSubmit={handleSubmit(addQuset)}>
                <div className="w-50 box p-3">
                    <input {...register("question")} className="w-100 mb-3" placeholder="Your question" type="text"/>
                    <div>
                        <div className="mb-2">
                            <input name="ans " className="me-2" type="radio"/>
                            <input {...register("answer 1")} placeholder="answer 1" type="text"/>
                        </div>
                        <div className="mb-2">
                            <input name="ans " className="me-2" type="radio"/>
                            <input {...register("answer 2")} placeholder="answer 2" type="text"/>
                        </div>
                        <div className="mb-2">
                            <input name="ans " className="me-2" type="radio"/>
                            <input {...register("answer 3")} placeholder="answer 3" type="text"/>
                        </div>
                        <div className="mb-2">
                            <input name="ans " className="me-2" type="radio"/>
                            <input {...register("answer 4")} placeholder="answer 4" type="text"/>
                        </div>
                    </div>
                    <div className="text-end mb-2 "><input {...register("points")} type="number" placeholder="points"/></div>

                    <div className="text-end">
                        <button>Add a question</button>
                        <button type="button">Create a test</button>
                    </div>
                </div>
            </form>

        </div>
    );
};

export default AddTest;
