import React from 'react';

const TestCard = ({test}) => {

    let userAnswer = []
    const addAnswer = (e) => {
        let a = {
            question: e.target.name,
            userAnswers: e.target.value,
            id: e.target.id
        }
        userAnswer.push(...userAnswer, a)
        console.log(userAnswer)
    }


    return (
        <div className="col-6">
            <form onChange={e => addAnswer(e)}>
                <div className="m-2">
                    <p>{test?.question}</p>
                    {test?.answers?.map(ans => {
                        return (
                            <div className="m-2">
                                <label>
                                    <input onChange={e => chooseAnswer(e, ans)} className="radio-inp" type="radio"
                                           name={test?.question} value={ans} id={test.id}/>

                                    {ans}</label>
                            </div>
                        )
                    })}
                </div>
            </form>
        </div>
    )
}

export default TestCard;
