import React, {useEffect, useState} from 'react';
import axios from "axios";
import TestCard from "../../components/TestCard/TestCard.js";
import {useParams} from "react-router-dom";
import ResCard from "../resCard/ResCard.js";


const PassTestCard = () => {
    const [test, setTest] = useState([])
    const [result, setResult] = useState([])
    const [show, setShow] = useState(false)
    const {name} = useParams()

    useEffect(() => {
        axios(`/api/tests/get-test/${name}`).then(({data}) => {
            setTest(data)
        })
    },[])

    const [questions, setQuestions] = useState([])

    const chooseAnswer = (e, quest) =>{
        const filtered = questions.filter(it => it.id !== quest.id)
        const questAns = {
            id: quest.id,
            userAnswer: e.target.value
        }
        setQuestions([...filtered, questAns])
    }



    const finishTest = () => {
        axios.post(`/api/tests/check-test/js`, questions )
            .then(({data}) => setResult(data))
        setShow(!show)

    }

    return (
        <section className="bg-brown">
            <div className="container">
                <div className="row">
                    {
                        test?.map(quest => {
                            return (
                                <TestCard test={quest} chooseAnswer={chooseAnswer}/>

                            )
                        } )
                    }
                </div>
                <button className="btn-secondary btn" onClick={finishTest}>Finish</button>
                {show && <ResCard result={result}/>}
            </div>
        </section>
    )

};

export default PassTestCard;
