import React, {useEffect, useState} from 'react';
import axios from "axios";
import TestCard from "../TestCard/TestCard.js";
import {useParams} from "react-router-dom";


const PassTestCard = () => {
    const [test, setTest] = useState([])
    const name = useParams()
    console.log(name)
    useEffect(() => {
        axios(`/api/tests/get-test/${name}`).then(({data}) => {
            setTest(data)
        })
    },[])

    const [questions, setQuestions] = useState([])

    const chooseAnswer = (e, quest) =>{
        const filtered = questions.filter(it => it.id !== quest.id)
        const questAns = {
            id: quest.id,
            userAnswer: e.target.value
        }
        setQuestions([...filtered, questAns])
    }



    const finishTest = () => {
        axios.post(`/api/tests/check-test/js`, questions )
            .then(({data}) => console.log(data))
    }

    return (
        <div className="container">
            <div className="row">
                {
                    test?.map(quest => {
                        return (
                            <div key={quest.id} className="col-6">
                                        <p>{quest?.question}</p>
                                        {quest?.answers?.map((ans, idx) => {
                                            return (
                                                <div key={idx} className="m-2">
                                                    <label>
                                                        <input onChange={e => chooseAnswer(e, quest)} className="radio-inp" type="radio"
                                                               name={quest?.id} value={ans} />

                                                        {ans}</label>
                                                </div>
                                            )
                                        })}

                            </div>
                        )
                    } )
                }
            </div>
            <button className="btn-secondary btn" onClick={finishTest}>finish</button>
        </div>
    )

};

export default PassTestCard;
